import React from 'react'
import { Link } from 'react-router-dom'

import Container from 'react-bootstrap/Container';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Button from 'react-bootstrap/Button';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const style = {
	container: {
		height: '100%', 
		padding: '30px'
	},

	containerH1: {
		color: "#fff",
	},

	buttonText: {
		color: '#007bff'
	},

	img: {
		height: '25%'
	}
}

const Home = () => (
	<Container className="d-flex flex-column justify-content-around align-items-center text-center" style={style}>
		<img
			alt=""
			src={require('./image-home.png')}
			className="d-inline-block align-top"
			style={style.img}
		/>
		<h1 style={style.containerH1}>
			Desenvolva seus conhecimentos jurídicos! <br/> 
			<small> 
				Viaje pelo mundo processual e resolva situações da prática profissional, baseado em casos reais! 
			</small> 
		</h1>
		<p>
			<Link to="/modules/">
		   		<Button variant="light" style={style.buttonText}>
		   			JOGAR <FontAwesomeIcon icon="play" /> 
		   		</Button>
		   	</Link>
		</p>
	</Container>
)

export default Home
